// Login & Logout Section

const loginBtn = document.getElementById("loginBtn");
loginBtn.addEventListener("click", login);

function login() {
  const user = document.getElementById("user").value;
  const password = document.getElementById("password").value;
  if (user == "sajibmsc@gmail.com" && password == "sajib") {
    const loginArea = document.getElementById("login-area");
    loginArea.style.display = "none";
    console.log(loginArea);
    const dashboardArea = document.getElementById("dashboard");
    dashboardArea.style.display = "block";
    console.log(dashboardArea);
    const t = document.querySelector("title");
    t.innerHTML = "Dashboard";
  } else {
    alert("User/Password is wrong");
  }
  document.getElementById("user").value = "";
  document.getElementById("password").value = "";
}

const logoutBtn = document.getElementById("logout");
logoutBtn.addEventListener("click", () => {
  const loginArea = document.getElementById("login-area");
  loginArea.style.display = "block";
  console.log(loginArea);
  const dashboardArea = document.getElementById("dashboard");
  dashboardArea.style.display = "none";
  console.log(dashboardArea);
});

// Deposit section
const depositBtn = document.getElementById("depositBtn");
depositBtn.addEventListener("click", depositMoney);

function depositMoney() {
  const depositAmount = parseFloat(
    document.getElementById("depositAmount").value
  );
  if (depositAmount > 0) {
    const recentDeposit = parseFloat(
      document.getElementById("recentDeposit").innerText
    );
    const totalDeposit = depositAmount + recentDeposit;
    document.getElementById("recentDeposit").innerText = totalDeposit;
    const balance = parseFloat(
      document.getElementById("totalBalance").innerText
    );
    const totalBalance = depositAmount + balance;
    document.getElementById("totalBalance").innerText = totalBalance;
  } else {
    alert("Wrong Amount");
  }
  document.getElementById("depositAmount").value = "";
}

// Withdrawal  Section
const withdrawBtn = document.getElementById("withdrawBtn");
withdrawBtn.addEventListener("click", withdrawMoney);

function withdrawMoney() {
  const withdrawAmount = parseFloat(
    document.getElementById("withdrawAmount").value
  );
  if (withdrawAmount > 0) {
    const balance = parseFloat(
      document.getElementById("totalBalance").innerText
    );
    if (balance >= withdrawAmount && balance > 0) {
      const recentWithdrawal = parseFloat(
        document.getElementById("recentWithdrawal").innerText
      );
      const totalWithdraw = withdrawAmount + recentWithdrawal;
      document.getElementById("recentWithdrawal").innerText = totalWithdraw;

      const totalBalance = balance - withdrawAmount;
      document.getElementById("totalBalance").innerText = totalBalance;
    } else {
      alert("Insufficient Balance");
    }
  } else {
    alert("Wrong Amount");
  }
  document.getElementById("withdrawAmount").value = "";
}
